Widget addToFeedButtons({name, url}) {
  return Padding(
    padding: const EdgeInsets.all(10.0),
    child: Row(
      children: [
        CircleAvatar(
          backgroundImage: NetworkImage('$url'),
          radius: 20,
        ),
        SizedBox(width: 10),
        Text(
          '$name',
          style: TextStyle(
            fontWeight: FontWeight.bold,
          ),
        ),
        Spacer(),
        FlatButton(
          child: Text(
            '+Follow',
            style: TextStyle(color: Colors.blue),
          ),
          onPressed: null,
        )
      ],
    ),
  );
}

